function add_buttons_layout(itemTemplate){
    rowLayout = jQuery('<div class="row" style="margin-left: 64px;"> \
                            <div class="column"> <div class="bp-plus" onclick="add_person_js(this)" name="plus">+</div> </div>\
                            <div class="bp-button column" onclick="add_person_js(this)" name="edit">\u270e</div> \
                            <div class="bp-button column" onclick="remove_person_js(this)" name="remove">x</div> \
                        </div>')
    itemTemplate.append(rowLayout);
}

primitives.orgdiagram.Template.prototype._getDefaultItemTemplate = function () {
	var contentSize = new primitives.common.Size(this.itemSize),
		itemTemplate,
		titleBackground,
		title,
		photoborder,
		photo,
		description;
		contentSize=contentSize
	contentSize.width -= (this.boxModel ? this.itemBorderWidth * 2 : 0);
	contentSize.height -= (this.boxModel ? this.itemBorderWidth * 2 : 0);


	itemTemplate = jQuery("<div onclick='add_link_js(this)'></div>")
        .css({
			"border-width": this.itemBorderWidth + "px"
        }).addClass("bp-item bp-corner-all bt-item-frame");

        
    add_buttons_layout(itemTemplate);

	titleBackground = jQuery('<div name="titleBackground"></div>')
		.css({
			top: "20px",
			left: "64px",
			width: (contentSize.width - 65) + "px",
			height: "48px"
        }).addClass("bp-item bp-corner-all bp-title-frame");

	itemTemplate.append(titleBackground);

    person_id = jQuery('<input type="text" name="person_id" value="" hidden />')
 	itemTemplate.append(person_id);

 	years = jQuery('<div name="years"></div>').addClass("bp-item years");
 	itemTemplate.append(years);

    is_alive = jQuery('<input type="text" name="alive" value="" hidden />')
 	itemTemplate.append(is_alive);

	title = jQuery('<div name="title"></div>')
		.css({
			top: "3px",
			left: "4px",
			width: (contentSize.width - 62 - 4 * 2) + "px",
			height: "48px"
		}).addClass("bp-item bp-title");

	titleBackground.append(title);

	photoborder = jQuery("<div></div>")
		.css({
			top: "7px",
			left: "2px",
			width: "60px",
			
		}).addClass("bp-item bp-photo-frame");

	itemTemplate.append(photoborder);

	photo = jQuery('<img name="photo" alt=""></img>')
		.css({
			width: "60px",

		});
	photoborder.append(photo);

	description = jQuery('<div name="description"></div>')
	.css({
		top: "51px",
		left: "67px",
		width: (contentSize.width - 4 - 63) + "px",
		height: "48px"
	}).addClass("bp-item bp-description");

	itemTemplate.append(description);

	return itemTemplate.wrap('<div>').parent().html();
};